import React, {useState} from 'react';
import ExpenseItem from "./ExpenseItem";
import './Expenses.css';
import './ExpensesFilter';
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {
    const [filteredYear, setFilteredYear] = useState('2020');
    const filterChangeHandler = (selectedFilterYear) => {
        setFilteredYear(selectedFilterYear);
    };
    console.log(props.items[0].date.getFullYear());
    return (
        <div>
            <Card className="expenses">
                <ExpensesFilter selected={filteredYear} onChangeFilterYear={filterChangeHandler}/>

                {props.items.filter(expense => (
                    expense.date.getFullYear() == filteredYear
                )).map(expense => (
                    <ExpenseItem 
                        key={expense.id}
                        title={expense.title} 
                        amount={expense.amount} 
                        date={expense.date}
                    />
                ))}
            </Card>
        </div>
    );
}

export default Expenses;
